import { useCallback, useEffect } from "react";

const useInfiniteScroll = ({ containerRef, successFunc, topOffset = 30 } = {}) => {

  const handleListener = () => {
    debugger
    if (
      window.scrollY + window.innerHeight >=
      document.documentElement.scrollHeight
    ) {
      successFunc();
    }
  };

  useEffect(() => {
    containerRef.current.addEventListener("scroll", handleListener);

    const cleanup = () => containerRef.current.removeEventListener("scroll", handleListener);

    return cleanup;
  }, [successFunc]);

  return "";
};

export default useInfiniteScroll;
