import { useState } from "react";

import PosterImage from "./poster-image";
import MovieTileModal from "./movie-modal";

import { MovieResponse } from "./types";

const MovieCard = ({ title, poster_path: path, popularity, overview }: MovieResponse) => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  return (
    <>
      <div className="movie-card" onClick={() => setIsModalOpen(true)}>
        <PosterImage imgSrc={path} title={title} />
        <div className="movie-desc">
          <div className="title">{title}</div>
          <div className="rating">Rating: {popularity}</div>
          <div className="movie-cardHover">
            <p>{overview}</p>
          </div>
        </div>
      </div>
      {isModalOpen && <MovieTileModal closeModal={() => setIsModalOpen(false)} />}
    </>
  );
};

export default MovieCard;
